```
let obj = {
    name: '123',
    age: '11'
}
let proxyObj = new Proxy(obj,{
    deleteProperty(trapTarget,key){
        if(key === 'name'){
            return false
        }
        return Reflect.deleteProperty(trapTarget,key)
    }
})
console.log(delete proxyObj.name)//false
console.log(delete proxyObj.age)//true
