function compose(...funcs) {
  if (funcs.length === 0) {
    return (arg) => arg;
  }

  if (funcs.length === 1) {
    return funcs[0];
  }
  const f = funcs.reduce((a, b) => (...args) => a(b(...args)));
  return f;
}

